var link=document.querySelector(".contact-button"),popup=document.querySelector(".popup"),close=document.querySelector(".popup-close"),form=popup.querySelector("form"),login=form.querySelector("[name=name]"),mail=form.querySelector("[name=email]"),storageLogin=localStorage.getItem("login"),storageMail=localStorage.getItem("mail");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("popup-show"),storageLogin?(login.value=storageLogin,mail.focus()):login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("popup-show","popup-error")}),form.addEventListener("submit",function(e){login.value&&mail.value?(localStorage.setItem("login",login.value),localStorage.setItem("mail",mail.value)):(e.preventDefault(),popup.classList.remove("popup-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("popup-error"))}),window.addEventListener("keydown",function(e){27==e.keyCode&&popup.classList.contains("popup-show")&&popup.classList.remove("popup-show","popup-error")});